mapper.select:
select
id, account, nickname, password, salt, dep_id, job_id, state, phone, email, birthday, identify, avatar,
create_time
from shop_mall.sys_user


        and id = #{id}


        and account = #{account}


        and nickname = #{nickname}


        and password = #{password}


        and salt = #{salt}


        and dep_id = #{depId}


        and job_id = #{jobId}


        and state = #{state}


        and phone = #{phone}


        and email = #{email}


        and birthday = #{birthday}


        and identify = #{identify}


        and avatar = #{avatar}


        and create_time = #{createTime}

select su.*,sd.dep_name department,sj.job_name job,GROUP_CONCAT(sr.role_name) as role
from sys_user su
left join sys_department sd on sd.id = su.dep_id
left join sys_job sj on sj.id = su.job_id
left join sys_user_role_relation surr on surr.user_id = su.id
left join sys_role sr on sr.id = surr.role_id

    su.id != 1 and sr.role != 'client'

        and su.nickname like CONCAT(#{nickname},'%')


        and su.dep_id = #{departmentId}


        and su.job_id = #{jobId}


        and su.state = #{state}


group by su.id
limit #{offset},#{limit}

SELECT count(*) FROM ( SELECT count(*) FROM sys_user su LEFT JOIN sys_department sd ON sd.id = su.dep_id LEFT JOIN sys_job sj ON sj.id = su.job_id LEFT JOIN sys_user_role_relation surr ON surr.user_id = su.id LEFT JOIN sys_role sr ON sr.id = surr.role_id
sr.role != 'client' and su.id != 1
and su.nickname like CONCAT(#{nickname},'%')
and su.dep_id = #{departmentId}
and su.job_id = #{jobId}
and su.state = #{state}
GROUP BY su.id ) a
