mapper.select:
select
id, goods_id, count, u_id
from shop_mall.user_cart


        and id = #{id}


        and goods_id = #{goodsId}


        and count = #{count}


        and u_id = #{uId}

select uc.*, g.goods_name, substring_index(g.imgs, ',', 1) as url,su.nickname from user_cart uc left join goods g on g.id = uc.goods_id left join sys_user su on uc.u_id = su.id
su.nickname like CONCAT(#{nickname},'%')
limit #{offset},#{limit}

mapper.delete:
delete from user_cart where id
in

    #{id}

mapper.update:
update shop_mall.user_cart
set count = #{count,jdbcType=INTEGER}
where goods_id = #{goodsId,jdbcType=INTEGER}
  and u_id = #{uId,jdbcType=INTEGER}

mapper.select:
select *
from user_cart
where goods_id = #{goodsId,jdbcType=INTEGER}
  and u_id = #{uId,jdbcType=INTEGER}
